insert into dua(cat_id, title, dua_text, meaning, source) values(7,'Dua For Jannah','رَبَّنَا وَأَدْخِلْهُمْ جَنَّاتِ عَدْنٍ الَّتِي وَعَدتَّهُمْ وَمَن صَلَحَ مِنْ آبَائِهِمْ وَأَزْوَاجِهِمْ وَذُرِّيَّاتِهِمْ ۚ إِنَّكَ أَنتَ الْعَزِيزُ الْحَكِيمُ وَقِهِمُ السَّيِّئَاتِ ۚ وَمَن تَقِ السَّيِّئَاتِ يَوْمَئِذٍ فَقَدْ رَحِمْتَهُ ۚ وَذَٰلِكَ هُوَ الْفَوْزُ الْعَظِيمُ','Our Lord! And make them enter the Adn (Eden) Paradise (everlasting Gardens) which you have promised them, and to the righteous among their fathers, their wives, and their offspring! Verily, You are the All-Mighty, the All-Wise. And save them from (the punishment, because of what they did of) the sins, and whomsoever You save from (the punishment, because of what they did of) the sins (i.e. excuse them) that Day, him verily, You have taken into mercy." And that is the supreme success.','40:8-9');
insert into dua(cat_id, title, dua_text, meaning, source) values(7,'Dua For Seeking Forgiveness And Protection From Hellfire','رَبَّنَا وَسِعْتَ كُلَّ شَيْءٍ رَّحْمَةً وَعِلْمًا فَاغْفِرْ لِلَّذِينَ تَابُوا وَاتَّبَعُوا سَبِيلَكَ وَقِهِمْ عَذَابَ الْجَحِيمِ','Our Lord, You have encompassed all things in mercy and knowledge, so forgive those who have repented and followed Your way and protect them from the punishment of Hellfire.','40:7');
insert into dua(cat_id, title, dua_text, meaning, source) values(8,'Dua 1','اَللَّهُمَّ إِنِّي أَسْأَلُكَ عِلْمًا نَافِعًا, وَ رِزْقًا طَيَّبًا, وَ عَمَلاً مُتَقَبَّل','O Lord, give us in this world that which is good and in the Hereafter that which is good, and save us from the punishment of the Fire.','Ibn Majah');
insert into dua(cat_id, title, dua_text, meaning, source) values(9,'Dua 1','رَبَّنَا تَقَبَّلۡ مِنَّآۖ إِنَّكَ أَنتَ ٱلسَّمِيعُ ٱلۡعَلِيمُ','Our Lord, accept [this] from us. Indeed You are the Hearing, the Knowing.','2:127');
insert into dua(cat_id, title, dua_text, meaning, source) values(9,'Dua 2','رَبَّنَآ ءَاتِنَا فِي ٱلدُّنۡيَا حَسَنَةٗ وَفِي ٱلۡأٓخِرَةِ حَسَنَةٗ وَقِنَا عَذَابَ ٱلنَّارِ','Our Lord, give us in this world [that which is] good and in the Hereafter [that which is] good and protect us from the punishment of the Fire.','2:201');
insert into dua(cat_id, title, dua_text, meaning, source) values(9,'Dua 3','رَبَّنَآ أَفۡرِغۡ عَلَيۡنَا صَبۡرٗا وَثَبِّتۡ أَقۡدَامَنَا وَٱنصُرۡنَا عَلَى ٱلۡقَوۡمِ ٱلۡكَٰفِرِينَ','Our Lord, pour upon us patience and plant firmly our feet and give us victory over the disbelieving people.','2:250');
insert into dua(cat_id, title, dua_text, meaning, source) values(9,'Dua 4','رَبَّنَا لَا تُزِغۡ قُلُوبَنَا بَعۡدَ إِذۡ هَدَيۡتَنَا وَهَبۡ لَنَا مِن لَّدُنكَ رَحۡمَةًۚ إِنَّكَ أَنتَ ٱلۡوَهَّابُ','Our Lord, let not our hearts deviate after You have guided us and grant us from Yourself mercy. Indeed, You are the Bestower.','3:8');
insert into dua(cat_id, title, dua_text, meaning, source) values(9,'Dua 5','رَبَّنَآ إِنَّنَآ ءَامَنَّا فَٱغْفِرْ لَنَا ذُنُوبَنَا وَقِنَا عَذَابَ ٱلنَّارِ','Our Lord, indeed we have believed, so forgive us our sins and protect us from the punishment of the Fire','3:16');
insert into dua(cat_id, title, dua_text, meaning, source) values(9,'Dua 6','رَبَّنَا فَٱغۡفِرۡ لَنَا ذُنُوبَنَا وَكَفِّرۡ عَنَّا سَيِّـَٔاتِنَا وَتَوَفَّنَا مَعَ ٱلۡأَبۡرَارِ','Our Lord, so forgive us our sins and remove from us our misdeeds and cause us to die among the righteous.','3:193');
insert into dua(cat_id, title, dua_text, meaning, source) values(9,'Dua 7','رَبَّنَا ظَلَمْنَآ أَنفُسَنَا وَإِن لَّمْ تَغْفِرْ لَنَا وَتَرْحَمْنَا لَنَكُونَنَّ مِنَ ٱلْخَٰسِرِينَ','Our Lord, we have wronged ourselves, and if You do not forgive us and have mercy upon us, we will surely be among the losers.','7:23');
insert into dua(cat_id, title, dua_text, meaning, source) values(10,'Dua For Sehri','وَبِصَوْمِ غَدٍ نَّوَيْتُ مِنْ شَهْرِ رَمَضَانَ','I intend to keep the fast for tomorrow in the month of Ramadan','');
insert into dua(cat_id, title, dua_text, meaning, source) values(10,'Dua For Iftar ','ذَهَبَ الظَّمَأُ وَابْتَلَّتِ الْعُرُوقُ وَثَبَتَ الأَجْرُ إِنْ شَاءَ اللَّهُ','Thirst is gone, the veins are moistened and the reward is certain if Allah wills','Abu Dawud: 2357');
insert into dua(cat_id, title, dua_text, meaning, source) values(10,'Dua On First 10 Days ','يَا حَيُّ يَا قَيُّومُ بِرَحْمَتِكَ أَسْتَغيثُ','Oh Everliving, The Everlasting, I seek Your help through Your mercy.','');
insert into dua(cat_id, title, dua_text, meaning, source) values(10,'Dua On Second 10 Days','اَسْتَغْفِرُ اللہَ رَبِّی مِنْ کُلِّ زَنْبٍ وَّ اَتُوْبُ اِلَیْہِ','I seek forgiveness from Allah for all my sins and turn to Him.','');
insert into dua(cat_id, title, dua_text, meaning, source) values(10,'Dua On Third 10 Days','اَللَّهُمَّ أَجِرْنِي مِنَ النَّارِ','O Allah, save me from the fire (Jahannam).','');
insert into dua(cat_id, title, dua_text, meaning, source) values(10,'Dua On Laylatul Qadr','اللَّهُمَّ إِنَّكَ عَفُوٌّ كَرِيمٌ تُحِبُّ الْعَفْوَ فَاعْفُ عَنِّي','O Allah, indeed You are Pardoning, Generous, You love pardon, so pardon me','Tirmidhi: 3513');
